import boto3

def delete_all_files_in_bucket(bucket_name):
    s3 = boto3.client('s3')

    objects_to_delete = s3.list_objects_v2(Bucket=bucket_name)

    if 'Contents' in objects_to_delete:

        object_keys = [{'Key': obj['Key']} for obj in objects_to_delete['Contents']]

        s3.delete_objects(
            Bucket=bucket_name,
            Delete={
                'Objects': object_keys
            }
        )

        print(f"Deleted {len(object_keys)} files from {bucket_name}")
    else:
        print(f"The bucket {bucket_name} is already empty.")


def get_valid_input():
    while True:
        try:
            print("\nWhich buckets content do you want to delete?")
            print("1. superhero-pics")
            print("2. prod-superhero-pics")
            user_input = int(input("\nEnter 1 or 2: "))

            if user_input == 1 or user_input == 2:
                return user_input 
            else:
                print("Invalid input. Please enter either 1 or 2.")
        except ValueError:
            print("Invalid input. Please enter a number.")


user_choice = get_valid_input()

if user_choice == 1:
    bucket_name = "superhero-pics"
else:
    bucket_name = "prod-superhero-pics"

print(f"\nYou choose: {bucket_name}")

confirm = input(f"Are you SURE you want to permanently delete the contents of the \"{bucket_name}\" bucket? (y/n) : ")


if confirm.upper().strip() in ["YES", "Y"]:
    delete_all_files_in_bucket(bucket_name)
else:
    print("Process canceled. The contents of your S3 bucket were not deleted.")

