    return foundNote ? foundNote.starred : false;
  }

  clearAllStarredNotes(): void {
    const notes = this.notesSubject.value.map(note => ({ ...note, starred: false }));

    this.notesSubject.next(notes);
    this.starredStatusChangedSubject.next();
  }

}

