package com.springbackend.demo.services;

import com.springbackend.demo.models.User;
import com.springbackend.demo.repositories.UserRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.util.Optional;

@Service
public class UserService {

	@Autowired
	private UserRepository userRepository;

	public User saveUser(User user) {
		if (userRepository.findByFirstname(user.getFirstname()) != null) {
			throw new IllegalArgumentException("User with the name " + user.getFirstname() + " already exists");
		}

		return userRepository.save(user);
	}

	public Optional<User> getUserByFirstname(String firstname) {
		return Optional.ofNullable(userRepository.findByFirstname(firstname));
	}
}

