package com.springbackend.demo.controllers;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

import com.springbackend.demo.models.User;
import com.springbackend.demo.services.UserService;

@RestController
@RequestMapping("/users")
public class UserController {

    @Autowired
    private UserService userService;

    @PostMapping
    public ResponseEntity<?> createUser(@RequestBody User user) {
	try {
	    User createdUser = userService.saveUser(user);
	    return ResponseEntity.status(201).body(createdUser);
	} catch (IllegalArgumentException e) {
	    return ResponseEntity.status(400).body(e.getMessage());
	}
    }

    @GetMapping("/{firstname}")
    public ResponseEntity<User> getUserByFirstname(@PathVariable String firstname) {
	return userService.getUserByFirstname(firstname)
	    .map(user -> ResponseEntity.ok().body(user))
	    .orElseGet(() -> ResponseEntity.status(404).body(null));
    }
}

